package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.stream.Collectors;
import java.util.List;
import java.util.Optional;
import java.util.Set;
import org.springframework.core.convert.ConversionService;
import org.ebs.model.AssayGeneModel;
import org.ebs.services.to.AssayGeneTo;
import org.ebs.model.repos.AssayGeneRepository;
import org.ebs.services.AssayGeneService;
import org.ebs.services.TraitService;
import org.ebs.model.repos.TraitRepository;
import org.ebs.services.to.TraitTo;
import org.ebs.model.repos.MarkerRepository;
import org.ebs.services.MarkerService;
import org.ebs.services.to.MarkerTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:09:17 PM
 */
@Component @Validated
public class AssayGeneResolver implements GraphQLResolver<AssayGeneTo> {

	private AssayGeneService assaygeneService;
	private AssayGeneRepository assaygeneRepository;
	@Autowired
	private ConversionService converter;
	private TraitService traitService;
	private TraitRepository traitRepository;
	private MarkerService markerService;
	private MarkerRepository markerRepository;

	/**
	 * 
	 * @param markerRepository
	 * @param markerService
	 * @param traitRepository
	 * @param traitService
	 * @param assaygeneRepository
	 * @param assaygeneService
	 */
	@Autowired
	public AssayGeneResolver(MarkerRepository markerRepository, MarkerService markerService, TraitRepository traitRepository, TraitService traitService, AssayGeneRepository assaygeneRepository, AssayGeneService assaygeneService){
		this.assaygeneService = assaygeneService; 
		this.assaygeneRepository = assaygeneRepository; 
		this.traitService = traitService; 
		this.traitRepository = traitRepository; 
		this.markerService = markerService; 
		this.markerRepository = markerRepository; 
	
	}

	/**
	 * 
	 * @param assaygeneTo
	 */
	public Set<MarkerTo> getMarkers(AssayGeneTo assaygeneTo){
		return markerRepository.findByAssaygeneId(assaygeneTo.getId()).stream() 
		 .map(e -> converter.convert(e,MarkerTo.class)) 
		 .collect(Collectors.toSet());
	}

	/**
	 * 
	 * @param assaygeneTo
	 */
	public TraitTo getTrait(AssayGeneTo assaygeneTo){
		AssayGeneModel assaygeneModel = assaygeneRepository.findById(assaygeneTo.getId()).get(); 
		 return traitService.findTrait(assaygeneModel.getTrait().getId()).get();
	}

}