package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.AssayclassTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.AssayclassService;
import org.ebs.services.to.TechnologyPlatformTo;
import org.ebs.services.to.ServiceTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 07-Sep-2020 8:10:52 AM
 */
@Component @Validated
public class AssayclassResolver implements GraphQLResolver<AssayclassTo> {

	private AssayclassService assayclassService;

	/**
	 * 
	 * @param assayclassService
	 */
	@Autowired
	public AssayclassResolver(AssayclassService assayclassService){
		this.assayclassService = assayclassService; 
	
	}

	/**
	 * 
	 * @param assayclassTo
	 */
	public ServiceTo getService(AssayclassTo assayclassTo){
		return assayclassService.findService(assayclassTo.getId()).get();
	}

	/**
	 * 
	 * @param assayclassTo
	 */
	public Set<TechnologyPlatformTo> getTechnologyPlatforms(AssayclassTo assayclassTo){
		return assayclassService.findTechnologyPlatforms(assayclassTo.getId());
	}

}