package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.CfTypeTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.CfTypeService;
import org.ebs.services.to.NodeCfTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:22 AM
 */
@Component @Validated
public class CfTypeResolver implements GraphQLResolver<CfTypeTo> {

	private CfTypeService cftypeService;

	/**
	 * 
	 * @param cftypeService
	 */
	@Autowired
	public CfTypeResolver(CfTypeService cftypeService){
		this.cftypeService = cftypeService; 
	
	}

	/**
	 * 
	 * @param cftype
	 */
	public Set<NodeCfTo> getNodecfs(CfTypeTo cftype){
		return cftypeService.findNodeCfs(cftype.getId());
	}

}