package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.CfValueTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import org.ebs.services.CfValueService;
import org.ebs.services.to.RequestTo;
import org.ebs.services.to.NodeCfTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:23 AM
 */
@Component @Validated
public class CfValueResolver implements GraphQLResolver<CfValueTo> {

	private CfValueService cfvalueService;

	/**
	 * 
	 * @param cfvalueService
	 */
	@Autowired
	public CfValueResolver(CfValueService cfvalueService){
		this.cfvalueService = cfvalueService; 
	
	}

	/**
	 * 
	 * @param cfvalueTo
	 */
	public NodeCfTo getNodecf(CfValueTo cfvalueTo){
		return cfvalueService.findNodeCf(cfvalueTo.getId()).get();
	}

	/**
	 * 
	 * @param cfvalueTo
	 */
	public RequestTo getRequest(CfValueTo cfvalueTo){
		return cfvalueService.findRequest(cfvalueTo.getId()).get();
	}

}