@Component @Validated
public class CollaboratorResolver implements GraphQLResolver<CollaboratorTo> {

	private CollaboratorService collaboratorService;
	private CollaboratorRepository collaboratorRepository;
	@Autowired
	private ConversionService converter;
	private PearsonService pearsonService;
	private PearsonRepository pearsonRepository;

	/**
	 * 
	 * @param pearsonRepository
	 * @param pearsonService
	 * @param collaboratorRepository
	 * @param collaboratorService
	 */
	@Autowired
	public CollaboratorResolver(PearsonRepository pearsonRepository, PearsonService pearsonService, CollaboratorRepository collaboratorRepository, CollaboratorService collaboratorService){
		this.collaboratorService = collaboratorService; 
		this.collaboratorRepository = collaboratorRepository; 
		this.pearsonService = pearsonService; 
		this.pearsonRepository = pearsonRepository; 
	
	}

	/**
	 * 
	 * @param collaboratorTo
	 */
	public PearsonTo getPearson(CollaboratorTo collaboratorTo){
		CollaboratorModel collaboratorModel = collaboratorRepository.findById(collaboratorTo.getId()).get(); 
		 return pearsonService.findPearson(collaboratorModel.getPearson().getId()).get();
	}

}