package org.ebs.graphql.resolvers;

import org.ebs.services.to.MessageTo;
import org.ebs.services.to.ModuleTo;
import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.ComponentTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.ComponentService;
import org.ebs.services.to.InstanceTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 15-Jun-2020 5:13:22 PM
 */
@Component @Validated
public class ComponentResolver implements GraphQLResolver<ComponentTo> {

	private ComponentService componentService;

	/**
	 * 
	 * @param componentService
	 */
	@Autowired
	public ComponentResolver(ComponentService componentService){
		this.componentService = componentService; 
	
	}

	/**
	 * 
	 * @param component
	 */
	public Set<InstanceTo> getInstances(ComponentTo component){
		return componentService.findInstances(component.getId());
	}

	/**
	 * 
	 * @param component
	 */
	public Set<MessageTo> getMessages(ComponentTo component){
		return componentService.findMessages(component.getId());
	}

	/**
	 * 
	 * @param component
	 */
	public Set<ModuleTo> getModules(ComponentTo component){
		return componentService.findModules(component.getId());
	}

}