package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.stream.Collectors;
import java.util.List;
import java.util.Optional;
import java.util.Set;
import org.springframework.core.convert.ConversionService;
import org.ebs.model.CountryModel;
import org.ebs.services.to.CountryTo;
import org.ebs.model.repos.CountryRepository;
import org.ebs.services.CountryService;
import org.ebs.model.repos.VendorRepository;
import org.ebs.services.VendorService;
import org.ebs.services.to.VendorTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:09:27 PM
 */
@Component @Validated
public class CountryResolver implements GraphQLResolver<CountryTo> {

	private CountryService countryService;
	private CountryRepository countryRepository;
	@Autowired
	private ConversionService converter;
	private VendorService vendorService;
	private VendorRepository vendorRepository;

	/**
	 * 
	 * @param vendorRepository
	 * @param vendorService
	 * @param countryRepository
	 * @param countryService
	 */
	@Autowired
	public CountryResolver(VendorRepository vendorRepository, VendorService vendorService, CountryRepository countryRepository, CountryService countryService){
		this.countryService = countryService; 
		this.countryRepository = countryRepository; 
		this.vendorService = vendorService; 
		this.vendorRepository = vendorRepository; 
	
	}

	/**
	 * 
	 * @param countryTo
	 */
	public Set<VendorTo> getVendors(CountryTo countryTo){
		return vendorRepository.findByCountryId(countryTo.getId()).stream() 
		 .map(e -> converter.convert(e,VendorTo.class)) 
		 .collect(Collectors.toSet());
	}

}