package org.ebs.graphql.resolvers;

import org.ebs.services.CropService;
import org.ebs.services.to.ProgramTo;
import org.ebs.services.to.ServiceProviderTo;
import org.ebs.services.to.SeasonTo;
import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.CropTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 04-Sep-2020 10:04:54 AM
 */
@Component @Validated
public class CropResolver implements GraphQLResolver<CropTo> {

	private CropService cropService;

	/**
	 * 
	 * @param cropService
	 */
	@Autowired
	public CropResolver(CropService cropService){
		this.cropService = cropService; 
	
	}

	/**
	 * 
	 * @param crop
	 */
	public Set<ProgramTo> getPrograms(CropTo crop){
		return cropService.findprograms(crop.getId());
	}

	/**
	 * 
	 * @param cropTo
	 */
	public Set<SeasonTo> getSeasons(CropTo cropTo){
		return cropService.findseasons(cropTo.getId());
	}

	/**
	 * 
	 * @param cropTo
	 */
	public Set<ServiceProviderTo> getServiceproviders(CropTo cropTo){
		return cropService.findserviceproviders(cropTo.getId());
	}

}