package org.ebs.graphql.resolvers;

import org.ebs.model.CustomerModel;
import org.ebs.services.to.CustomerTo;
import org.ebs.model.repos.CustomerRepository;
import org.ebs.services.CustomerService;
import org.ebs.model.repos.OrganizationRepository;
import org.ebs.services.OrganizationService;
import org.ebs.services.to.OrganizationTo;
import org.ebs.model.repos.TenantRepository;
import org.ebs.services.TenantService;
import org.ebs.services.to.TenantTo;
import com.coxautodev.graphql.tools.GraphQLResolver;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.stream.Collectors;
import java.util.List;
import java.util.Optional;
import java.util.Set;
import org.springframework.core.convert.ConversionService;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 28-Apr-2020 11:58:11 PM
 */
@Component @Validated
public class CustomerResolver implements GraphQLResolver<CustomerTo> {

	private CustomerService customerService;
	private CustomerRepository customerRepository;
	@Autowired
	private ConversionService converter;
	private OrganizationService organizationService;
	private OrganizationRepository organizationRepository;
	private TenantService tenantService;
	private TenantRepository tenantRepository;

	/**
	 * 
	 * @param tenantRepository
	 * @param tenantService
	 * @param organizationRepository
	 * @param organizationService
	 * @param customerRepository
	 * @param customerService
	 */
	@Autowired
	public CustomerResolver(TenantRepository tenantRepository, TenantService tenantService, OrganizationRepository organizationRepository, OrganizationService organizationService, CustomerRepository customerRepository, CustomerService customerService){
		this.customerService = customerService; 
		this.customerRepository = customerRepository; 
		this.organizationService = organizationService; 
		this.organizationRepository = organizationRepository; 
		this.organizationService = organizationService; 
		this.organizationRepository = organizationRepository; 
		this.tenantService = tenantService; 
		this.tenantRepository = tenantRepository; 
	
	}

	/**
	 * 
	 * @param customerTo
	 */
	public OrganizationTo getOrganization(CustomerTo customerTo){
		CustomerModel customerModel = customerRepository.findById(customerTo.getId()).get(); 
		 return organizationService.findOrganization(customerModel.getOrganization().getId()).get();
	}

	/**
	 * 
	 * @param customerTo
	 */
	public Set<OrganizationTo> getOrganizations(CustomerTo customerTo){
		return organizationRepository.findByCustomerId(customerTo.getId()).stream() 
		 .map(e -> converter.convert(e,OrganizationTo.class)) 
		 .collect(Collectors.toSet());
	}

	/**
	 * 
	 * @param customerTo
	 */
	public Set<TenantTo> getTenants(CustomerTo customerTo){
		return tenantRepository.findByCustomerId(customerTo.getId()).stream() 
		 .map(e -> converter.convert(e,TenantTo.class)) 
		 .collect(Collectors.toSet());
	}

}