package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.DataFormatTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.DataFormatService;
import org.ebs.services.to.VendorTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 10-Sep-2020 12:19:09 AM
 */
@Component @Validated
public class DataFormatResolver implements GraphQLResolver<DataFormatTo> {

	private DataFormatService dataformatService;

	/**
	 * 
	 * @param dataformatService
	 */
	@Autowired
	public DataFormatResolver(DataFormatService dataformatService){
		this.dataformatService = dataformatService; 
	
	}

	/**
	 * 
	 * @param dataformat
	 */
	public Set<VendorTo> getVendors(DataFormatTo dataformat){
		return dataformatService.findVendors(dataformat.getId());
	}

}