package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.EmailTemplateTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.EmailTemplateService;
import org.ebs.services.to.EntityReferenceTo;
import org.ebs.services.to.TenantTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 07-Sep-2020 8:11:14 AM
 */
@Component @Validated
public class EmailTemplateResolver implements GraphQLResolver<EmailTemplateTo> {

	private EmailTemplateService emailTemplateService;

	/**
	 * 
	 * @param emailtemplateService
	 */
	@Autowired
	public EmailTemplateResolver(EmailTemplateService emailtemplateService){
		this.emailTemplateService = emailtemplateService; 
	
	}

	/**
	 * 
	 * @param emailtemplateTo
	 */
	public Set<EntityReferenceTo> getEntityReferences(EmailTemplateTo emailtemplateTo){
		return emailTemplateService.findEntityReferences(emailtemplateTo.getId());
	}

	/**
	 * 
	 * @param emailTemplateTo
	 */
	public TenantTo getTenant(EmailTemplateTo emailTemplateTo){
		return emailTemplateService.findTenant(emailTemplateTo.getId()).get();
	}

}