package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.EntityReferenceTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.EntityReferenceService;
import org.ebs.services.to.SegmentTo;
import org.ebs.services.to.AttributesTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Jul-2020 5:06:34 PM
 */
@Component @Validated
public class EntityReferenceResolver implements GraphQLResolver<EntityReferenceTo> {

	private EntityReferenceService entityreferenceService;

	/**
	 * 
	 * @param entityreferenceService
	 */
	@Autowired
	public EntityReferenceResolver(EntityReferenceService entityreferenceService){
		this.entityreferenceService = entityreferenceService; 
	
	}

	/**
	 * 
	 * @param entityreference
	 */
	public Set<AttributesTo> getAttributess(EntityReferenceTo entityreference){
		return entityreferenceService.findAttributess(entityreference.getId());
	}

	/**
	 * 
	 * @param entityreference
	 */
	public Set<SegmentTo> getSegments(EntityReferenceTo entityreference){
		return entityreferenceService.findSegments(entityreference.getId());
	}

}