package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.EventTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import org.ebs.services.EventService;
import org.ebs.services.to.StageTo;
import org.ebs.services.to.WfInstanceTo;
import org.ebs.services.to.NodeTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:29 AM
 */
@Component @Validated
public class EventResolver implements GraphQLResolver<EventTo> {

	private EventService eventService;

	/**
	 * 
	 * @param eventService
	 */
	@Autowired
	public EventResolver(EventService eventService){
		this.eventService = eventService; 
	
	}

	/**
	 * 
	 * @param eventTo
	 */
	public NodeTo getNode(EventTo eventTo){
		return eventService.findNode(eventTo.getId()).get();
	}

	/**
	 * 
	 * @param eventTo
	 */
	public StageTo getStage(EventTo eventTo){
		return eventService.findStage(eventTo.getId()).get();
	}

	/**
	 * 
	 * @param eventTo
	 */
	public WfInstanceTo getWfinstance(EventTo eventTo){
		return eventService.findWfInstance(eventTo.getId()).get();
	}

}