package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.FormulaTypeTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.FormulaTypeService;
import org.ebs.services.to.SegmentTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:30 AM
 */
@Component @Validated
public class FormulaTypeResolver implements GraphQLResolver<FormulaTypeTo> {

	private FormulaTypeService formulatypeService;

	/**
	 * 
	 * @param formulatypeService
	 */
	@Autowired
	public FormulaTypeResolver(FormulaTypeService formulatypeService){
		this.formulatypeService = formulatypeService; 
	
	}

	/**
	 * 
	 * @param formulatype
	 */
	public Set<SegmentTo> getSegments(FormulaTypeTo formulatype){
		return formulatypeService.findSegments(formulatype.getId());
	}

}