package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.FunctionalUnitTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.FunctionalUnitService;
import org.ebs.services.to.ProgramTo;
import org.ebs.services.to.UserTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:19:49 AM
 */
@Component @Validated
public class FunctionalUnitResolver implements GraphQLResolver<FunctionalUnitTo> {

	private FunctionalUnitService functionalunitService;

	/**
	 * 
	 * @param functionalunitService
	 */
	@Autowired
	public FunctionalUnitResolver(FunctionalUnitService functionalunitService){
		this.functionalunitService = functionalunitService; 
	
	}

	/**
	 * 
	 * @param functionalunit
	 */
	public Set<FunctionalUnitTo> getFunctionalunits(FunctionalUnitTo functionalunit){
		return functionalunitService.findFunctionalUnits(functionalunit.getId());
	}

	/**
	 * 
	 * @param functionalunitTo
	 */
	public ProgramTo getProgram(FunctionalUnitTo functionalunitTo){
		return functionalunitService.findProgram(functionalunitTo.getId()).get();
	}

	/**
	 * 
	 * @param functionalunitTo
	 */
	public Set<UserTo> getUsers(FunctionalUnitTo functionalunitTo){
		return functionalunitService.findUsers(functionalunitTo.getId());
	}

}