package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.InstanceTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.InstanceService;
import org.ebs.services.to.AuditLogsTo;
import org.ebs.services.to.DomainTo;
import org.ebs.services.to.TenantTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:32 AM
 */
@Component @Validated
public class InstanceResolver implements GraphQLResolver<InstanceTo> {

	private InstanceService instanceService;

	/**
	 * 
	 * @param instance
	 */
	public Set<AuditLogsTo> getAuditlogss(InstanceTo instance){
		return instanceService.findAuditLogss(instance.getId());
	}

	/**
	 * 
	 * @param instanceTo
	 */
	public Set<DomainTo> getDomains(InstanceTo instanceTo){
		return instanceService.findDomains(instanceTo.getId());
	}

	/**
	 * 
	 * @param instanceTo
	 */
	public TenantTo getTenant(InstanceTo instanceTo){
		return instanceService.findTenant(instanceTo.getId()).get();
	}

	/**
	 * 
	 * @param instanceService
	 */
	@Autowired
	public InstanceResolver(InstanceService instanceService){
		this.instanceService = instanceService; 
	
	}

}