package org.ebs.graphql.resolvers;

import java.util.Set;
import org.ebs.services.MessageService;
import org.ebs.services.to.TranslationTo;
import org.ebs.services.to.ComponentTo;
import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.MessageTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Jul-2020 5:06:42 PM
 */
@Component @Validated
public class MessageResolver implements GraphQLResolver<MessageTo> {

	private MessageService messageService;

	/**
	 * 
	 * @param messageTo
	 */
	public ComponentTo getComponent(MessageTo messageTo){
		return messageService.findComponent(messageTo.getId()).get();
	}

	/**
	 * 
	 * @param message
	 */
	public Set<TranslationTo> getTranslations(MessageTo message){
		return messageService.findTranslations(message.getId());
	}

	/**
	 * 
	 * @param messageService
	 */
	@Autowired
	public MessageResolver(MessageService messageService){
		this.messageService = messageService; 
	
	}

}