package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.NodeCfTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.NodeCfService;
import org.ebs.services.to.CfValueTo;
import org.ebs.services.to.CfTypeTo;
import org.ebs.services.to.HtmlTagTo;
import org.ebs.services.to.EntityReferenceTo;
import org.ebs.services.to.NodeTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:37 AM
 */
@Component @Validated
public class NodeCfResolver implements GraphQLResolver<NodeCfTo> {

	private NodeCfService nodecfService;

	/**
	 * 
	 * @param nodecfTo
	 */
	public CfTypeTo getCftype(NodeCfTo nodecfTo){
		return nodecfService.findCfType(nodecfTo.getId()).get();
	}

	/**
	 * 
	 * @param nodecf
	 */
	public Set<CfValueTo> getCfvalues(NodeCfTo nodecf){
		return nodecfService.findCfValues(nodecf.getId());
	}

	/**
	 * 
	 * @param nodecfTo
	 */
	public EntityReferenceTo getEntityreference(NodeCfTo nodecfTo){
		return nodecfService.findEntityReference(nodecfTo.getId()).get();
	}

	/**
	 * 
	 * @param nodecfTo
	 */
	public HtmlTagTo getHtmltag(NodeCfTo nodecfTo){
		return nodecfService.findHtmlTag(nodecfTo.getId()).get();
	}

	/**
	 * 
	 * @param nodecfTo
	 */
	public NodeTo getNode(NodeCfTo nodecfTo){
		return nodecfService.findNode(nodecfTo.getId()).get();
	}

	/**
	 * 
	 * @param nodecfService
	 */
	@Autowired
	public NodeCfResolver(NodeCfService nodecfService){
		this.nodecfService = nodecfService; 
	
	}

}