package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.stream.Collectors;
import java.util.List;
import java.util.Optional;
import java.util.Set;
import org.springframework.core.convert.ConversionService;
import org.ebs.model.PartnerModel;
import org.ebs.services.to.PartnerTo;
import org.ebs.model.repos.PartnerRepository;
import org.ebs.services.PartnerService;
import org.ebs.services.PearsonService;
import org.ebs.model.repos.PearsonRepository;
import org.ebs.services.to.PearsonTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 28-Apr-2020 12:07:08 AM
 */
@Component @Validated
public class PartnerResolver implements GraphQLResolver<PartnerTo> {

	private PartnerService partnerService;
	private PartnerRepository partnerRepository;
	@Autowired
	private ConversionService converter;
	private PearsonService pearsonService;
	private PearsonRepository pearsonRepository;

	/**
	 * 
	 * @param partnerTo
	 */
	public PearsonTo getPearson(PartnerTo partnerTo){
		PartnerModel partnerModel = partnerRepository.findById(partnerTo.getId()).get(); 
		 return pearsonService.findPearson(partnerModel.getPearson().getId()).get();
	}

	/**
	 * 
	 * @param pearsonRepository
	 * @param pearsonService
	 * @param partnerRepository
	 * @param partnerService
	 */
	@Autowired
	public PartnerResolver(PearsonRepository pearsonRepository, PearsonService pearsonService, PartnerRepository partnerRepository, PartnerService partnerService){
		this.partnerService = partnerService; 
		this.partnerRepository = partnerRepository; 
		this.pearsonService = pearsonService; 
		this.pearsonRepository = pearsonRepository; 
	
	}

}