package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.PersonStatusTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.PersonStatusService;
import org.ebs.services.to.PersonTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:46 AM
 */
@Component @Validated
public class PersonStatusResolver implements GraphQLResolver<PersonStatusTo> {

	private PersonStatusService personstatusService;

	/**
	 * 
	 * @param personstatus
	 */
	public Set<PersonTo> getPersons(PersonStatusTo personstatus){
		return personstatusService.findPersons(personstatus.getId());
	}

	/**
	 * 
	 * @param personstatusService
	 */
	@Autowired
	public PersonStatusResolver(PersonStatusService personstatusService){
		this.personstatusService = personstatusService; 
	
	}

}