package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.ProcessTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.ProcessService;
import org.ebs.services.to.NodeTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:49 AM
 */
@Component @Validated
public class ProcessResolver implements GraphQLResolver<ProcessTo> {

	private ProcessService processService;

	/**
	 * 
	 * @param process
	 */
	public Set<NodeTo> getNodes(ProcessTo process){
		return processService.findNodes(process.getId());
	}

	/**
	 * 
	 * @param processService
	 */
	@Autowired
	public ProcessResolver(ProcessService processService){
		this.processService = processService; 
	
	}

}