package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.ProductTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.ProductService;
import org.ebs.services.to.ProductFunctionTo;
import org.ebs.services.to.DomainTo;
import org.ebs.services.to.HtmlTagTo;
import org.ebs.services.to.NodeTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:51 AM
 */
@Component @Validated
public class ProductResolver implements GraphQLResolver<ProductTo> {

	private ProductService productService;

	/**
	 * 
	 * @param productTo
	 */
	public DomainTo getDomain(ProductTo productTo){
		return productService.findDomain(productTo.getId()).get();
	}

	/**
	 * 
	 * @param productTo
	 */
	public HtmlTagTo getHtmltag(ProductTo productTo){
		return productService.findHtmlTag(productTo.getId()).get();
	}

	/**
	 * 
	 * @param product
	 */
	public Set<NodeTo> getNodes(ProductTo product){
		return productService.findNodes(product.getId());
	}

	/**
	 * 
	 * @param product
	 */
	public Set<ProductFunctionTo> getProductfunctions(ProductTo product){
		return productService.findProductFunctions(product.getId());
	}

	/**
	 * 
	 * @param productService
	 */
	@Autowired
	public ProductResolver(ProductService productService){
		this.productService = productService; 
	
	}

}