package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.ReportTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import org.ebs.services.ReportService;
import org.ebs.services.to.ProductTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:20:01 AM
 */
@Component @Validated
public class ReportResolver implements GraphQLResolver<ReportTo> {

	private ReportService reportService;

	/**
	 * 
	 * @param reportTo
	 */
	public ProductTo getProduct(ReportTo reportTo){
		return reportService.findProduct(reportTo.getId()).get();
	}

	/**
	 * 
	 * @param reportService
	 */
	@Autowired
	public ReportResolver(ReportService reportService){
		this.reportService = reportService; 
	
	}

}