package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.StageTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.StageService;
import org.ebs.services.to.ProductFunctionTo;
import org.ebs.services.to.PhaseTo;
import org.ebs.services.to.EventTo;
import org.ebs.services.to.HtmlTagTo;
import org.ebs.services.to.NodeTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:55 AM
 */
@Component @Validated
public class StageResolver implements GraphQLResolver<StageTo> {

	private StageService stageService;

	/**
	 * 
	 * @param stage
	 */
	public Set<EventTo> getEvents(StageTo stage){
		return stageService.findEvents(stage.getId());
	}

	/**
	 * 
	 * @param stageTo
	 */
	public HtmlTagTo getHtmltag(StageTo stageTo){
		return stageService.findHtmlTag(stageTo.getId()).get();
	}

	/**
	 * 
	 * @param stageTo
	 */
	public Set<NodeTo> getNodes(StageTo stageTo){
		return stageService.findNodes(stageTo.getId());
	}

	/**
	 * 
	 * @param stageTo
	 */
	public PhaseTo getPhase(StageTo stageTo){
		return stageService.findPhase(stageTo.getId()).get();
	}

	/**
	 * 
	 * @param stage
	 */
	public Set<ProductFunctionTo> getProductfunctions(StageTo stage){
		return stageService.findProductFunctions(stage.getId());
	}

	/**
	 * 
	 * @param stageService
	 */
	@Autowired
	public StageResolver(StageService stageService){
		this.stageService = stageService; 
	
	}

}