package org.ebs.graphql.resolvers;

import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import org.ebs.services.StatusService;
import org.ebs.services.to.StatusTypeTo;
import org.ebs.services.to.WfInstanceTo;
import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.StatusTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:56 AM
 */
@Component @Validated
public class StatusResolver implements GraphQLResolver<StatusTo> {

	private StatusService statusService;

	/**
	 * 
	 * @param statusTo
	 */
	public StatusTypeTo getStatustype(StatusTo statusTo){
		return statusService.findStatusType(statusTo.getId()).get();
	}

	/**
	 * 
	 * @param statusTo
	 */
	public WfInstanceTo getWfinstance(StatusTo statusTo){
		return statusService.findWfInstance(statusTo.getId()).get();
	}

	/**
	 * 
	 * @param statusService
	 */
	@Autowired
	public StatusResolver(StatusService statusService){
		this.statusService = statusService; 
	
	}

}