package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.StatusTypeTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.StatusTypeService;
import org.ebs.services.to.StatusTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:57 AM
 */
@Component @Validated
public class StatusTypeResolver implements GraphQLResolver<StatusTypeTo> {

	private StatusTypeService statustypeService;

	/**
	 * 
	 * @param statustype
	 */
	public Set<StatusTo> getStatuss(StatusTypeTo statustype){
		return statustypeService.findStatuss(statustype.getId());
	}

	/**
	 * 
	 * @param statustypeService
	 */
	@Autowired
	public StatusTypeResolver(StatusTypeService statustypeService){
		this.statustypeService = statustypeService; 
	
	}

}