package org.ebs.graphql.resolvers;

import java.util.Set;
import java.util.stream.Collectors;

import org.ebs.model.repos.PreferenceRepository;
import org.ebs.model.repos.StyleThemeRepository;
import org.ebs.services.PreferenceService;
import org.ebs.services.StyleThemeService;
import org.ebs.services.to.PreferenceTo;
import org.ebs.services.to.StyleThemeTo;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.core.convert.ConversionService;
import org.springframework.stereotype.Component;
import org.springframework.validation.annotation.Validated;

import com.coxautodev.graphql.tools.GraphQLResolver;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:22 PM
 */
@Component @Validated
public class StyleThemeResolver implements GraphQLResolver<StyleThemeTo> {

	private StyleThemeService stylethemeService;
	private StyleThemeRepository stylethemeRepository;
	@Autowired
	private ConversionService converter;
	private PreferenceService preferenceService;
	private PreferenceRepository preferenceRepository;

	/**
	 * 
	 * @param stylethemeTo
	 */
	public Set<PreferenceTo> getPreferences(StyleThemeTo stylethemeTo){
		return preferenceRepository.findByStylethemeId(stylethemeTo.getId()).stream() 
		 .map(e -> converter.convert(e,PreferenceTo.class)) 
		 .collect(Collectors.toSet());
	}

	/**
	 * 
	 * @param preferenceRepository
	 * @param preferenceService
	 * @param stylethemeRepository
	 * @param stylethemeService
	 */
	@Autowired
	public StyleThemeResolver(PreferenceRepository preferenceRepository, PreferenceService preferenceService, StyleThemeRepository stylethemeRepository, StyleThemeService stylethemeService){
		this.stylethemeService = stylethemeService; 
		this.stylethemeRepository = stylethemeRepository; 
		this.preferenceService = preferenceService; 
		this.preferenceRepository = preferenceRepository; 
	
	}

}