package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.StyleThemeTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.StyleThemeService;
import org.ebs.services.to.PreferenceTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:57 AM
 */
@Component @Validated
public class StyleThemeResolver implements GraphQLResolver<StyleThemeTo> {

	private StyleThemeService stylethemeService;

	/**
	 * 
	 * @param styletheme
	 */
	public Set<PreferenceTo> getPreferences(StyleThemeTo styletheme){
		return stylethemeService.findPreferences(styletheme.getId());
	}

	/**
	 * 
	 * @param stylethemeService
	 */
	@Autowired
	public StyleThemeResolver(StyleThemeService stylethemeService){
		this.stylethemeService = stylethemeService; 
	
	}

}