package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.stream.Collectors;
import java.util.List;
import java.util.Optional;
import java.util.Set;
import org.springframework.core.convert.ConversionService;
import org.ebs.model.ThemeStyleModel;
import org.ebs.services.to.ThemeStyleTo;
import org.ebs.model.repos.ThemeStyleRepository;
import org.ebs.services.ThemeStyleService;
import org.ebs.model.repos.PreferenceRepository;
import org.ebs.services.PreferenceService;
import org.ebs.services.to.PreferenceTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 27-Apr-2020 9:47:54 AM
 */
@Component @Validated
public class ThemeStyleResolver implements GraphQLResolver<ThemeStyleTo> {

	@Autowired
	private ConversionService converter;
	private PreferenceRepository preferenceRepository;
	private PreferenceService preferenceService;
	private ThemeStyleRepository themestyleRepository;
	private ThemeStyleService themestyleService;

	/**
	 * 
	 * @param themestyleTo
	 */
	public Set<PreferenceTo> getPreferences(ThemeStyleTo themestyleTo){
		return preferenceRepository.findByThemestyleId(themestyleTo.getId()).stream() 
		 .map(e -> converter.convert(e,PreferenceTo.class)) 
		 .collect(Collectors.toSet());
	}

	/**
	 * 
	 * @param preferenceRepository
	 * @param preferenceService
	 * @param themestyleRepository
	 * @param themestyleService
	 */
	@Autowired
	public ThemeStyleResolver(PreferenceRepository preferenceRepository, PreferenceService preferenceService, ThemeStyleRepository themestyleRepository, ThemeStyleService themestyleService){
		this.themestyleService = themestyleService; 
		this.themestyleRepository = themestyleRepository; 
		this.preferenceService = preferenceService; 
		this.preferenceRepository = preferenceRepository; 
	
	}

}