package org.ebs.graphql.resolvers;

import java.util.Set;
import java.util.stream.Collectors;

import org.ebs.model.repos.TraitClassRepository;
import org.ebs.model.repos.TraitRepository;
import org.ebs.services.TraitClassService;
import org.ebs.services.TraitService;
import org.ebs.services.to.TraitClassTo;
import org.ebs.services.to.TraitTo;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.core.convert.ConversionService;
import org.springframework.stereotype.Component;
import org.springframework.validation.annotation.Validated;

import com.coxautodev.graphql.tools.GraphQLResolver;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:27 PM
 */
@Component @Validated
public class TraitClassResolver implements GraphQLResolver<TraitClassTo> {

	private TraitClassService traitclassService;
	private TraitClassRepository traitclassRepository;
	@Autowired
	private ConversionService converter;
	private TraitService traitService;
	private TraitRepository traitRepository;

	/**
	 * 
	 * @param traitclassTo
	 */
	public Set<TraitTo> getTraits(TraitClassTo traitclassTo){
		return traitRepository.findByTraitclassId(traitclassTo.getId()).stream() 
		 .map(e -> converter.convert(e,TraitTo.class)) 
		 .collect(Collectors.toSet());
	}

	/**
	 * 
	 * @param traitRepository
	 * @param traitService
	 * @param traitclassRepository
	 * @param traitclassService
	 */
	@Autowired
	public TraitClassResolver(TraitRepository traitRepository, TraitService traitService, TraitClassRepository traitclassRepository, TraitClassService traitclassService){
		this.traitclassService = traitclassService; 
		this.traitclassRepository = traitclassRepository; 
		this.traitService = traitService; 
		this.traitRepository = traitRepository; 
	
	}

}