package org.ebs.graphql.resolvers;

import java.util.Set;
import java.util.stream.Collectors;

import org.ebs.model.TraitModel;
import org.ebs.model.repos.AssayGeneRepository;
import org.ebs.model.repos.TraitClassRepository;
import org.ebs.model.repos.TraitRepository;
import org.ebs.services.AssayGeneService;
import org.ebs.services.TraitClassService;
import org.ebs.services.TraitService;
import org.ebs.services.to.AssayGeneTo;
import org.ebs.services.to.TraitClassTo;
import org.ebs.services.to.TraitTo;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.core.convert.ConversionService;
import org.springframework.stereotype.Component;
import org.springframework.validation.annotation.Validated;

import com.coxautodev.graphql.tools.GraphQLResolver;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:10:28 PM
 */
@Component @Validated
public class TraitResolver implements GraphQLResolver<TraitTo> {

	private TraitService traitService;
	private TraitRepository traitRepository;
	@Autowired
	private ConversionService converter;
	private TraitClassService traitclassService;
	private TraitClassRepository traitclassRepository;
	private AssayGeneService assaygeneService;
	private AssayGeneRepository assaygeneRepository;

	/**
	 * 
	 * @param traitTo
	 */
	public Set<AssayGeneTo> getAssayGenes(TraitTo traitTo){
		return assaygeneRepository.findByTraitId(traitTo.getId()).stream() 
		 .map(e -> converter.convert(e,AssayGeneTo.class)) 
		 .collect(Collectors.toSet());
	}

	/**
	 * 
	 * @param traitTo
	 */
	public TraitClassTo getTraitClass(TraitTo traitTo){
		TraitModel traitModel = traitRepository.findById(traitTo.getId()).get(); 
		 return traitclassService.findTraitClass(traitModel.getTraitClass().getId()).get();
	}

	/**
	 * 
	 * @param assaygeneRepository
	 * @param assaygeneService
	 * @param traitclassRepository
	 * @param traitclassService
	 * @param traitRepository
	 * @param traitService
	 */
	@Autowired
	public TraitResolver(AssayGeneRepository assaygeneRepository, AssayGeneService assaygeneService, TraitClassRepository traitclassRepository, TraitClassService traitclassService, TraitRepository traitRepository, TraitService traitService){
		this.traitService = traitService; 
		this.traitRepository = traitRepository; 
		this.traitclassService = traitclassService; 
		this.traitclassRepository = traitclassRepository; 
		this.assaygeneService = assaygeneService; 
		this.assaygeneRepository = assaygeneRepository; 
	
	}

}