package org.ebs.graphql.resolvers;

import org.ebs.services.to.AttributesTo;
import org.ebs.services.to.LanguageTo;
import org.ebs.services.to.MessageTo;
import org.ebs.services.to.HtmlTagTo;
import org.ebs.services.to.ModuleTo;
import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.TranslationTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import org.ebs.services.TranslationService;
import org.ebs.services.to.AlertTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 15-Jun-2020 5:14:43 PM
 */
@Component @Validated
public class TranslationResolver implements GraphQLResolver<TranslationTo> {

	private TranslationService translationService;

	/**
	 * 
	 * @param translationTo
	 */
	public AlertTo getAlert(TranslationTo translationTo){
		return translationService.findAlert(translationTo.getId()).get();
	}

	/**
	 * 
	 * @param translationTo
	 */
	public AttributesTo getAttributes(TranslationTo translationTo){
		return translationService.findAttributes(translationTo.getId()).get();
	}

	/**
	 * 
	 * @param translationTo
	 */
	public HtmlTagTo getHtmltag(TranslationTo translationTo){
		return translationService.findHtmlTag(translationTo.getId()).get();
	}

	/**
	 * 
	 * @param translationTo
	 */
	public LanguageTo getLanguage(TranslationTo translationTo){
		return translationService.findLanguage(translationTo.getId()).get();
	}

	/**
	 * 
	 * @param translationTo
	 */
	public MessageTo getMessage(TranslationTo translationTo){
		return translationService.findMessage(translationTo.getId()).get();
	}

	/**
	 * 
	 * @param translationTo
	 */
	public ModuleTo getModule(TranslationTo translationTo){
		return translationService.findModule(translationTo.getId()).get();
	}

	/**
	 * 
	 * @param translationService
	 */
	@Autowired
	public TranslationResolver(TranslationService translationService){
		this.translationService = translationService; 
	
	}

}