package org.ebs.graphql.resolvers;

import org.ebs.services.to.WorkflowTo;
import org.ebs.services.to.RequestTo;
import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.WfInstanceTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.WfInstanceService;
import org.ebs.services.to.StatusTo;
import org.ebs.services.to.EventTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:03:02 AM
 */
@Component @Validated
public class WfInstanceResolver implements GraphQLResolver<WfInstanceTo> {

	private WfInstanceService wfinstanceService;

	/**
	 * 
	 * @param wfinstance
	 */
	public Set<EventTo> getEvents(WfInstanceTo wfinstance){
		return wfinstanceService.findEvents(wfinstance.getId());
	}

	/**
	 * 
	 * @param wfinstance
	 */
	public Set<RequestTo> getRequests(WfInstanceTo wfinstance){
		return wfinstanceService.findRequests(wfinstance.getId());
	}

	/**
	 * 
	 * @param wfinstance
	 */
	public Set<StatusTo> getStatuss(WfInstanceTo wfinstance){
		return wfinstanceService.findStatuss(wfinstance.getId());
	}

	/**
	 * 
	 * @param wfinstanceTo
	 */
	public WorkflowTo getWorkflow(WfInstanceTo wfinstanceTo){
		return wfinstanceService.findWorkflow(wfinstanceTo.getId()).get();
	}

	/**
	 * 
	 * @param wfinstanceService
	 */
	@Autowired
	public WfInstanceResolver(WfInstanceService wfinstanceService){
		this.wfinstanceService = wfinstanceService; 
	
	}

}