package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.WorkflowInstanceTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.WorkflowInstanceService;
import org.ebs.services.to.WorkflowTo;
import org.ebs.services.to.RequestTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 15-Jun-2020 5:14:55 PM
 */
@Component @Validated
public class WorkflowInstanceResolver implements GraphQLResolver<WorkflowInstanceTo> {

	private WorkflowInstanceService workflowinstanceService;

	/**
	 * 
	 * @param workflowinstance
	 */
	public Set<RequestTo> getRequests(WorkflowInstanceTo workflowinstance){
		return workflowinstanceService.findRequests(workflowinstance.getId());
	}

	/**
	 * 
	 * @param workflowinstanceTo
	 */
	public WorkflowTo getWorkflow(WorkflowInstanceTo workflowinstanceTo){
		return workflowinstanceService.findWorkflow(workflowinstanceTo.getId()).get();
	}

	/**
	 * 
	 * @param workflowinstanceService
	 */
	@Autowired
	public WorkflowInstanceResolver(WorkflowInstanceService workflowinstanceService){
		this.workflowinstanceService = workflowinstanceService; 
	
	}

}