package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.WorkflowTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.WorkflowService;
import org.ebs.services.to.PhaseTo;
import org.ebs.services.to.EntityReferenceTo;
import org.ebs.services.to.TenantTo;
import org.ebs.services.to.HtmlTagTo;
import org.ebs.services.to.WfInstanceTo;
import org.ebs.services.to.NodeTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:03:03 AM
 */
@Component @Validated
public class WorkflowResolver implements GraphQLResolver<WorkflowTo> {

	private WorkflowService workflowService;

	/**
	 * 
	 * @param workflowTo
	 */
	public EntityReferenceTo getEntityreference(WorkflowTo workflowTo){
		return workflowService.findEntityReference(workflowTo.getId()).get();
	}

	/**
	 * 
	 * @param workflowTo
	 */
	public HtmlTagTo getHtmltag(WorkflowTo workflowTo){
		return workflowService.findHtmlTag(workflowTo.getId()).get();
	}

	/**
	 * 
	 * @param workflowTo
	 */
	public NodeTo getNode(WorkflowTo workflowTo){
		return workflowService.findNode(workflowTo.getId()).get();
	}

	/**
	 * 
	 * @param workflow
	 */
	public Set<NodeTo> getNodes(WorkflowTo workflow){
		return workflowService.findNodes(workflow.getId());
	}

	/**
	 * 
	 * @param workflow
	 */
	public Set<PhaseTo> getPhases(WorkflowTo workflow){
		return workflowService.findPhases(workflow.getId());
	}

	/**
	 * 
	 * @param workflowTo
	 */
	public TenantTo getTenant(WorkflowTo workflowTo){
		return workflowService.findTenant(workflowTo.getId()).get();
	}

	/**
	 * 
	 * @param workflow
	 */
	public Set<WfInstanceTo> getWfinstances(WorkflowTo workflow){
		return workflowService.findWfInstances(workflow.getId());
	}

	/**
	 * 
	 * @param workflowService
	 */
	@Autowired
	public WorkflowResolver(WorkflowService workflowService){
		this.workflowService = workflowService; 
	
	}

}