package org.ebs.graphql.resolvers;

import com.coxautodev.graphql.tools.GraphQLResolver;
import org.ebs.services.to.teamTo;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.validation.annotation.Validated;
import java.util.Set;
import org.ebs.services.teamService;
import org.ebs.services.to.ProgramTo;
import org.ebs.services.to.PersonTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 09-Aug-2020 4:51:36 PM
 */
@Component @Validated
public class teamResolver implements GraphQLResolver<teamTo> {

	private teamService teamService;

	/**
	 * 
	 * @param teamTo
	 */
	public Set<PersonTo> getPersons(teamTo teamTo){
		return teamService.findPersons(teamTo.getId());
	}

	/**
	 * 
	 * @param teamTo
	 */
	public Set<ProgramTo> getPrograms(teamTo teamTo){
		return teamService.findPrograms(teamTo.getId());
	}

	/**
	 * 
	 * @param teamService
	 */
	@Autowired
	public teamResolver(teamService teamService){
		this.teamService = teamService; 
	
	}

}