package org.ebs.services;

import org.ebs.model.WfInstanceModel;
import org.ebs.model.repos.WfInstanceRepository;
import org.ebs.model.PersonModel;
import org.ebs.model.repos.PersonRepository;
import org.ebs.model.repos.RequestRepository;
import org.ebs.model.repos.CfValueRepository;
import org.springframework.transaction.annotation.Transactional;
import org.springframework.stereotype.Service;
import org.springframework.data.domain.Page;
import org.springframework.beans.factory.annotation.Autowired;
import java.util.List;
import java.util.Optional;
import org.ebs.util.FilterInput;
import org.ebs.util.PageInput;
import org.ebs.util.SortInput;
import org.ebs.util.Utils;
import org.springframework.core.convert.ConversionService;
import java.util.stream.Collectors;
import java.util.Set;
import org.ebs.services.to.RequestTo;
import org.ebs.services.to.Input.RequestInput;
import org.ebs.model.RequestModel;
import org.ebs.services.to.CfValueTo;
import org.ebs.services.to.WfInstanceTo;
import org.ebs.services.to.PersonTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:52 AM
 */
@Service @Transactional(readOnly = true)
  class RequestServiceImpl implements RequestService {

	private RequestRepository requestRepository;
	private ConversionService converter;
	private WfInstanceRepository wfinstanceRepository;
	private PersonRepository personRepository;
	private CfValueRepository cfvalueRepository;

	/**
	 * 
	 * @param Request
	 */
	@Override @Transactional(readOnly = false)
	public RequestTo createRequest(RequestInput Request){
		RequestModel model = converter.convert(Request,RequestModel.class); 
		 model.setId(0);
		 WfInstanceModel wfinstanceModel = wfinstanceRepository.findById(Request.getWfinstance().getId()).get(); 
		model.setWfinstance(wfinstanceModel); 
		PersonModel personModel = personRepository.findById(Request.getPerson().getId()).get(); 
		model.setPerson(personModel); 
		 
		 model= requestRepository.save(model); 
		 return converter.convert(model, RequestTo.class); 
	}

	/**
	 * 
	 * @param requestId
	 */
	@Override @Transactional(readOnly = false)
	public int deleteRequest(int requestId){
		RequestModel request = requestRepository.findById(requestId).orElseThrow(() -> new RuntimeException("Request not found")); 
		 request.setDeleted(true); 
		  requestRepository.save(request); 
		 return requestId;
	}

	/**
	 * 
	 * @param requestId
	 */
	public Set<CfValueTo> findCfValues(int requestId){
		return cfvalueRepository.findByRequestId(requestId).stream().map(e -> converter.convert(e,CfValueTo.class)).collect(Collectors.toSet());
	}

	/**
	 * 
	 * @param requestId
	 */
	public Optional<PersonTo> findPerson(int requestId){
		return requestRepository.findById(requestId).map(r -> converter.convert(r.getPerson(),PersonTo.class));
	}

	/**
	 * 
	 * @param requestId
	 */
	@Override
	public Optional<RequestTo> findRequest(int requestId){
		if(requestId <1) 
		 {return Optional.empty();} 
		 return requestRepository.findById(requestId).filter(r -> !r.getDeleted().booleanValue()).map(r -> converter.convert(r,RequestTo.class));
	}

	/**
	 * 
	 * @param page
	 * @param sort
	 * @param filters
	 */
	@Override
	public Page<RequestTo> findRequests(PageInput page, SortInput sort, List<FilterInput> filters){
		return requestRepository.findByCriteria(RequestModel.class,filters,sort,page).map(r -> converter.convert(r,RequestTo.class));
	}

	/**
	 * 
	 * @param requestId
	 */
	public Optional<WfInstanceTo> findWfInstance(int requestId){
		return requestRepository.findById(requestId).map(r -> converter.convert(r.getWfinstance(),WfInstanceTo.class));
	}

	/**
	 * 
	 * @param request
	 */
	@Override @Transactional(readOnly = false)
	public RequestTo modifyRequest(RequestInput request){
		RequestModel target= requestRepository.findById(request.getId()).orElseThrow(() -> new RuntimeException("Request not found")); 
		 RequestModel source= converter.convert(request,RequestModel.class); 
		 Utils.copyNotNulls(source,target); 
		 return converter.convert(requestRepository.save(target), RequestTo.class);
	}

	/**
	 * 
	 * @param cfvalueRepository
	 * @param converter
	 * @param requestRepository
	 * @param personRepository
	 * @param wfinstanceRepository
	 */
	@Autowired
	public RequestServiceImpl(CfValueRepository cfvalueRepository, ConversionService converter, RequestRepository requestRepository, PersonRepository personRepository, WfInstanceRepository wfinstanceRepository){
		this.requestRepository =requestRepository; 
		 this.converter = converter;
		 this.cfvalueRepository = cfvalueRepository;
		 this.wfinstanceRepository = wfinstanceRepository;
		 this.personRepository = personRepository;
	}

}