package org.ebs.services.converter;

import org.ebs.model.ActionModel;
import org.ebs.services.to.Input.ActionInput;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:09:09 PM
 */
@Component
public class ActionConverterInput implements Converter<ActionInput,ActionModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ActionModel convert(ActionInput source){
		ActionModel target = new  ActionModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}