package org.ebs.services.converter;

import org.ebs.model.AddressModel;
import org.ebs.services.to.AddressTo;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:09:10 PM
 */
@Component
public class AddressConverter implements Converter<AddressModel,AddressTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public AddressTo convert(AddressModel source){
		AddressTo target = new  AddressTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}