package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.AddressModel;
import org.ebs.services.to.Input.AddressInput;
import org.ebs.services.to.AddressTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 28-Apr-2020 11:58:03 PM
 */
@Component
public class AddressConverterInput implements Converter<AddressInput,AddressModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public AddressModel convert(AddressInput source){
		AddressModel target = new  AddressModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}