package org.ebs.services.converter;

import org.ebs.services.to.AlertTo;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.AlertModel;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 15-Jun-2020 5:13:06 PM
 */
@Component
  class AlertConverter implements Converter<AlertModel,AlertTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public AlertTo convert(AlertModel source){
		AlertTo target = new  AlertTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}