package org.ebs.services.converter;

import org.springframework.beans.BeanUtils;
import org.ebs.model.AttributesModel;
import org.ebs.services.to.Input.AttributesInput;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 10-Sep-2020 12:18:53 AM
 */
@Component
  class AttributesConverterInput implements Converter<AttributesInput,AttributesModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public AttributesModel convert(AttributesInput source){
		AttributesModel target = new  AttributesModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}