package org.ebs.services.converter;

import org.ebs.model.AuditLogsModel;
import org.ebs.services.to.AuditLogsTo;
import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 17-May-2020 3:09:19 PM
 */
@Component
public class AuditLogsConverter implements Converter<AuditLogsModel,AuditLogsTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public AuditLogsTo convert(AuditLogsModel source){
		AuditLogsTo target = new  AuditLogsTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}