package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.BreedingCycleModel;
import org.ebs.services.to.BreedingCycleTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 02-May-2021 6:19:43 AM
 */
@Component
  class BreedingCycleConverter implements Converter<BreedingCycleModel,BreedingCycleTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public BreedingCycleTo convert(BreedingCycleModel source){
		BreedingCycleTo target = new  BreedingCycleTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}