package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.CfTypeModel;
import org.ebs.services.to.Input.CfTypeInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:22 AM
 */
@Component
  class CfTypeConverterInput implements Converter<CfTypeInput,CfTypeModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public CfTypeModel convert(CfTypeInput source){
		CfTypeModel target = new  CfTypeModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}