package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.CfValueModel;
import org.ebs.services.to.CfValueTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:23 AM
 */
@Component
  class CfValueConverter implements Converter<CfValueModel,CfValueTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public CfValueTo convert(CfValueModel source){
		CfValueTo target = new  CfValueTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}