package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.CfValueModel;
import org.ebs.services.to.Input.CfValueInput;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 13-Mar-2021 12:02:23 AM
 */
@Component
  class CfValueConverterInput implements Converter<CfValueInput,CfValueModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public CfValueModel convert(CfValueInput source){
		CfValueModel target = new  CfValueModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}