package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.ComponentModel;
import org.ebs.services.to.ComponentTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 27-Jul-2020 8:42:15 AM
 */
@Component
  class ComponentConverter implements Converter<ComponentModel,ComponentTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public ComponentTo convert(ComponentModel source){
		ComponentTo target = new  ComponentTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}