package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.CustomerModel;
import org.ebs.services.to.Input.CustomerInput;
import org.ebs.services.to.CustomerTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 27-Apr-2020 9:47:36 AM
 */
@Component
public class CustomerConverter implements Converter<CustomerModel,CustomerTo> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public CustomerTo convert(CustomerModel source){
		CustomerTo target = new  CustomerTo(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}