package org.ebs.services.converter;

import org.springframework.core.convert.converter.Converter;
import org.springframework.stereotype.Component;
import org.springframework.beans.BeanUtils;
import org.ebs.model.CustomerModel;
import org.ebs.services.to.Input.CustomerInput;
import org.ebs.services.to.CustomerTo;

/**
 * @author EBRIONES
 * @version 1.0
 * @created 28-Apr-2020 11:58:10 PM
 */
@Component
public class CustomerConverterInput implements Converter<CustomerInput,CustomerModel> {

	/**
	 * 
	 * @param source
	 */
	@Override
	public CustomerModel convert(CustomerInput source){
		CustomerModel target = new  CustomerModel(); 
		BeanUtils.copyProperties(source, target); 
		return target;
	}

}